import './App.css';
import {useState} from "react";
import Axios from 'axios';

function App() {
  const [name,setName] = useState("");
  const [type,setType] = useState("");
  const [date,setDate] = useState("");
  const [review,setReview] = useState("");

  const [newDate, setNewDate] = useState("");

  const [movieList, setMovieList] = useState([]);

  const addMovie = () => {
    Axios.post('http://localhost:3001/create', {
      name: name, 
      type: type,
      date: date, 
      review: review,
    }).then(()=>{
      setMovieList([
        ...movieList,
        {
          name: name,
          type: type,
          date: date,
          review: review,
        },
      ])
    });
  }

  const getMovies = () => {
    Axios.get ('http://localhost:3001/movies').then((response)=>{
      setMovieList(response.data);
    });
  }

  const updateMovieDate = (id) => {
    Axios.put ('http://localhost:3001/update', {
      date: newDate,
      id: id
    }).then((response)=>{
      alert("update")
    });
  }

  return (
    <div className="App">
      <div className="information">
        <label>Name:</label>
        <input 
          type = "text" 
          onChange ={(event) => {
            setName(event.target.value);
          }}
        />
        <label>Type:</label>
        <input 
          type = "text" 
          onChange ={(event) => {
            setType(event.target.value);
          }}
        />
        <label> Date: </label>
        <input 
          type ="date"
          placeholder="dd-mm-yyyy"
          onChange ={(event) => {
            setDate(event.target.value);
          }}
        />
        <label> Review: </label>
        <input 
          type ="text"
          onChange ={(event) => {
            setReview(event.target.value);
          }}
        />
        <button onClick = {addMovie}>Add Movie Review</button>
      </div>
      <hr />
      <div className="movies">
        <button onClick = {getMovies}>show past movies</button>   

        {movieList.map((val, key) => {
          return (
          <div className = "movie">
            <div>
              <h3>Name: {val.name}</h3>
              <h3>Type: {val.type}</h3>
              <h3>Date: {val.date}</h3>
              <h3>Review: {val.review}</h3>
            </div>
            <div>
              <input 
                type = "text"
                placeholder = "dd-mm-yyyy"
                onChange ={(event) => {
                  setNewDate(event.target.value);
                }}
              />
              <button onClick = {()=>updateMovieDate(val.id)}>update</button>
            </div>
          </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
